function sum(arr) {
    let total = 0; // Fix: Initialize total as a local variable
    for (let num of arr) {
        total += num;
    }
    if (total === 0) {
        return 0;
    } else {
        return total;
    }
}
console.log(sum([1,2,3])); // Outputs the correct sum: 6
